}
else
{
	Script[EQ2Bot].Variable[DefensiveMode]:Set[FALSE]
	LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}]:AddSetting["Cast Defensive Spells",FALSE]
	Script[EQ2Bot].VariableScope.EQ2Bot:Save_Settings
