combobox.OnLeftClick:
variable index:item Items
variable iterator ItemIterator
variable int Counter = 1
This:ClearItems
Me:QueryInventory[Items, Location =- "Inventory" && Type =- "Weapon"]
Items:GetIterator[ItemIterator]
if ${ItemIterator:First(exists)}
{
	do
	{
		This:AddItem[${ItemIterator.Value.Name}]
	}
	while ${ItemIterator:Next(exists)}
}
do
{
	if ${Me.Equipment[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

variable index:item Items
variable iterator ItemIterator
variable int Counter = 1
This:ClearItems
Me:QueryInventory[Items, Location =- "Inventory" && Type =- "Weapon"]
Items:GetIterator[ItemIterator]
if ${ItemIterator:First(exists)}
{
	do
	{
		This:AddItem[${ItemIterator.Value.Name}]
	}
	while ${ItemIterator:Next(exists)}
}
do
{
	if ${Me.Equipment[${Counter}].Type.Equal[Weapon]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

variable index:item Items
variable iterator ItemIterator
variable int Counter = 1
This:ClearItems
Me:QueryInventory[Items, Location =- "Inventory" && (SubType =- "Staff" || SubType =- "Great Staff")]
Items:GetIterator[ItemIterator]
if ${ItemIterator:First(exists)}
{
	do
	{
		This:AddItem[${ItemIterator.Value.Name}]
	}
	while ${ItemIterator:Next(exists)}
}
do
{
	if ${Me.Equipment[${Counter}].SubType.Equal[Staff]} || ${Me.Equipment[${Counter}].SubType.Equal[Great Staff]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

