while ${Counter:Inc}<=22

variable index:item Items
variable iterator ItemIterator
variable int Counter = 1
This:ClearItems
Me:QueryInventory[Items, Location =- "Inventory" && (SubType =- "Staff" || WieldStyle =- "Two-Handed")]
Items:GetIterator[ItemIterator]
if ${ItemIterator:First(exists)}
{
	do
	{
		This:AddItem[${ItemIterator.Value.Name}]
	}
	while ${ItemIterator:Next(exists)}
}
do
{
	if ${Me.Equipment[${Counter}].SubType.Equal[Staff]} || ${Me.Equipment[${Counter}].WieldStyle.Equal[Two-Handed]]}
	{
		This:AddItem[${Me.Equipment[${Counter}].Name}]
	}
}
while ${Counter:Inc}<=22

