if ${This.Checked}
{
	Script[EQ2Bot].Variable[FocusMode]:Set[TRUE]
	LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}]:AddSetting["Use Focused Casting",TRUE]
