Items.OnSelect:
declare tmpvar int
LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}]:AddSet[BuffVenemousProc]
LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}].FindSet[BuffVenemousProc]:Clear

tmpvar:Set[1]
if ${This.SelectedItems}>0
{
do
{
LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}].FindSet[BuffVenemousProc]:AddSetting[${tmpvar},${This.SelectedItem[${tmpvar}].Text}]
}
while ${tmpvar:Inc}<=${This.SelectedItems}
}
Script[EQ2Bot].VariableScope.EQ2Bot:Save_Settings

Items.OnDeselect:
declare tmpvar int
LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}]:AddSet[BuffVenemousProc]
LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}].FindSet[BuffVenemousProc]:Clear

tmpvar:Set[1]
if ${This.SelectedItems}>0
{
do
{
LavishSettings[EQ2Bot].FindSet[Character].FindSet[${Me.SubClass}].FindSet[BuffVenemousProc]:AddSetting[${tmpvar},${This.SelectedItem[${tmpvar}].Text}]
}
while ${tmpvar:Inc}<=${This.SelectedItems}
}
Script[EQ2Bot].VariableScope.EQ2Bot:Save_Settings
