			This.ItemByValue[${SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[EQ2BotExtras].GetString[Shard Group Member]}]:Select


			Script[EQ2Bot].Variable[ShardGroupMember]:Set[${This.SelectedItem.Text}]
			SettingXML[Scripts/EQ2Bot/Character Config/${Me.Name}.xml].Set[EQ2BotExtras]:Set[Shard Group Member,${This.SelectedItem.Text}]:Save

			declare tmpvar int

			This:ClearItems
			tmpvar:Set[1]
			if ${Me.InRaid}
			{
				do
				{
					if ${Me.Raid[${tmpvar}].Name(exists)} && (${Me.Raid[${tmpvar}].Class.Equal[conjuror]} || ${Me.Raid[${tmpvar}].Class.Equal[necromancer]})
					{
						This:AddItem[${Me.Raid[${tmpvar}].Name}]
					}
				}
				while ${tmpvar:Inc}<=24
			}
			else
			{
				do
				{
					if ${Me.Group[${tmpvar}].Name(exists)} && (${Me.Group[${tmpvar}].Class.Equal[conjuror]} || ${Me.Group[${tmpvar}].Class.Equal[necromancer]})
					{
						This:AddItem[${Me.Group[${tmpvar}].Name}]
					}
				}
				while ${tmpvar:Inc}<=5
			}



